Ext.define('MyApp.store.MyJsonStore', {
    extend: 'Ext.data.Store',
    requires: [
        'MyApp.model.Event'
    ],

    config: {
        autoLoad: true,
        model: 'MyApp.model.Event',
        storeId: 'MyJsonStore',
        proxy: {
            type: 'ajax',
            url: 'http://unifeed.heroku.com/api-v2/this_is_hardcore/events.json?auth_token=unifeed-debug',
            reader: {
                type: 'json'
            }
        },
        grouper: {
            groupFn: function(record) {
                var dayDisplay = '';
                switch(record.get('start_datetime').getDate())
                {
                    case 9:
                    dayDisplay = 'Day 1';
                    break;
                    case 10:
                    dayDisplay = 'Day 2';
                    break;
                    case 11:
                    dayDisplay = 'Day 3';
                    break;   
                    case 12:
                    dayDisplay = 'Day 4';
                    break;
                }
                return dayDisplay + ' : ' + record.get('venue') + ' (' + record.get('start_date_display') + ')';
            }
        },
        sorters: {
            property: 'start_datetime'
        }
    }
});