Ext.define('MyApp.view.MyFormPanel', {
    extend: 'Ext.form.Panel',
    alias: 'widget.FormPanel',

    config: {
        items: [
            {
                xtype: 'image',
                cls: [
                    'artistImage'
                ],
                height: '200px',
                id: 'artistImage',
                width: '280px'
            },
            {
                xtype: 'fieldset',
                title: '',
                items: [
                    {
                        xtype: 'textareafield',
                        id: 'artistDescription',
                        name: 'description',
                        readOnly: true
                    },
                    {
                        xtype: 'textfield',
                        label: 'Start',
                        name: 'start_time_display',
                        readOnly: true
                    },
                    {
                        xtype: 'textfield',
                        label: 'End',
                        name: 'end_time_display',
                        readOnly: true
                    },
                    {
                        xtype: 'textfield',
                        label: 'Venue',
                        name: 'venue',
                        readOnly: true
                    },
                    {
                        xtype: 'button',
                        id: 'artistWebsiteButton',
                        itemId: 'artistWebsiteButton',
                        text: 'View Artist Website'
                    }
                ]
            }
        ],
        listeners: [
            {
                fn: 'onMybuttonTap',
                event: 'tap',
                delegate: '#artistWebsiteButton'
            }
        ]
    },

    onMybuttonTap: function(button, e, options) {
        navigator.app.loadUrl(this.artistWebsiteUrl);
    },

    loadEvent: function(event) {
        var image = Ext.getCmp("artistImage");
        image.setSrc(event.get("image_url"));
        this.setRecord(event);
        var artistWebsiteButton = Ext.getCmp('artistWebsiteButton');
        var artistWebsiteUrl = event.get('artist_website');
        if(artistWebsiteUrl)
        {
            this.artistWebsiteUrl = artistWebsiteUrl;
        }
        else
        {
            artistWebsiteButton.setHidden(true);
        }
    }

});